      type: "product",
      url: "/productos/ochio-tradicional",
      description: "Pan artesanal tradicional",
    },
    {
      id: "2",
      title: "Aceite de Oliva Virgen Extra",
      type: "product",
      url: "/productos/aceite-oliva",
      description: "Aceite premium de olivas seleccionadas",
    },
